import React, { useEffect, useRef } from "react";
import '../index.css';
import Lottie from "lottie-react";
import Animation from './Animation.json'

export default function Home() {
  const container = useRef(null);

  useEffect(() => {
    Lottie.loadAnimation({
      container: container.current,
      renderer: 'svg',
      loop: true,
      autoplay: true,
      animationData: Animation, // Use the imported Animation.json directly
    });
  }, []);

  return (<>    <div className="container" ref={container}></div>
    <div>
      <h1 className="home">
        Hi, I'm Shatlyk.
        <br />
        I am a passionate Full Stack Software Developer with a focus on creating innovative
        <br />
        and user-friendly web and mobile applications. With expertise in JavaScript, React,
        <br />
        Node.js, and React Native, I strive to deliver high-quality solutions that meet client
        <br />
        needs and exceed expectations.
      </h1>
      </div>
      </>

  );
}
