import htmlImg from "./assets/experience/html.png"

const getImageUrl = ( path ) => {
  return new URL(`assets/${path}`, import.meta.url).href;
};

const getImg = (title) => {

  let selectedImg = ""

  switch(title) {
    case "HTML":
      selectedImg = htmlImg
      break
    default:
      selectedImg = ""
  }

  return selectedImg
}

export default getImageUrl;
