export const getImageUrl = path => {
  return new URL(`assets/${path}`, import.meta.url).href;
};
export const getImg = title => {
  let selectedImg;
  switch (title) {
    case "HTML":
      selectedImg = htmlImg;
      break;
    case "CSS":
      selectedImg = cssImg;
      break;
    case "React":
      selectedImg = reactImg;
      break;
    case "JavaScript":
      selectedImg = javascriptImg;
      break;
    case "Node":
      selectedImg = nodeImg;
      break;
    case "Python":
      selectedImg = pythonImg;
      break;
    case "MongoDB":
      selectedImg = mongodbImg;
      break;
    case "Django":
      selectedImg = djangoImg;
      break;
    case "ga":
      selectedImg = galogoImg;
      break;
    case "pct":
      selectedImg = petronasImg;
      break;
    default:
      selectedImg = "";
  }
  return selectedImg;
};

