import React from "react";
import '../../index.js'
import './Home.css';
import Lottie from "lottie-react";
import Animation from '../../../src/assets/Animation.json';
import NavBar from '../NavBar/NavBar.js'
import Typewriter from "typewriter-effect";

function Home () {

  // const resumeURL = '../../../src/assets/Shatlyk resume.pdf';
  const resumeURL = process.env.PUBLIC_URL + '/assets/Shatlyk_resume.pdf';


  const openResume = () => {
    window.open(resumeURL, '_blank');
  };

  return (
    <>
      <div className="container">
        <div>
          <h1 className="home">
            <p>
              <Typewriter
                options={{
                  strings: ["Hey there, I'm Shatlyk!"],
                  autoStart: true,
                  loop: true,
                }}
              />
            </p>
            <br />
            I am a passionate Full Stack Software Developer with a focus on creating innovative
            <br />
            and user-friendly web and mobile applications. With expertise in JavaScript, React,
            <br />
            Node.js, and React Native, I strive to deliver high-quality solutions that meet client
            <br />
            needs and exceed expectations.
          </h1>
          <button className="resume-button" onClick={openResume}>See my Resume</button>
        </div>
        <div className="animation">
          <Lottie loop={true} animationData={Animation} />
        </div>
      </div>
    </>
  );
}

export default Home;
